query($userId:String, $profileId:String, $memberTypeId:String,$postId:String)
{
  getUserbyId(id:$userId) {
    id
    firstName
    lastName
    email
    subscribedToUserIds {
      firstName
      lastName
    }
  }
   getProfileById(id:$profileId){
    id
    avatar
    birthday
    sex
    country
    city
    street
    userId {
      firstName
    }
  }
  getPostById(id:$postId) {
    id
    title
    content
  }
  getMemberTypeById(id:$memberTypeId) {
    id
    discount
    monthPostsLimit
  }
}

